<?php/*Radford Reservation SystemAuthor: Andrew MeltonFilename: /lib/db_warning_functions.phpPurpose:This file contains all functions related to warnings in the databaseKnown Bugs/Fixes:None*/function getWarningType($type){	$types = array(RES_WARNING_ACTIVE => "Active Warning", RES_WARNING_NOTE => "Notification", RES_WARNING_INACTIVE => "Inactive Warning");	return $types[$type];}function warnUser($userid, $reason, $type){	$userid = makeStringSafe($userid);	$reason = makeStringSafe($reason);	$type = makeStringSafe($type);	doQuery("INSERT INTO ".getDBPrefix()."_warnings SET user_id = '".$userid."', reason='".$reason."', type='".$type."' , time='".getCurrentMySQLDateTime()."'");	sendWarningNotice($userid, $reason, $type);}function saveWarning($warnid, $reason, $type){	$warnid = makeStringSafe($warnid);	$reason = makeStringSafe($reason);	$type = makeStringSafe($type);	doQuery("UPDATE ".getDBPrefix()."_warnings SET reason = '".$reason."', type = '".$type."' WHERE warn_id = '".$warnid."'");}function getWarningsForUser($userid){	$userid = makeStringSafe($userid);	return doQuery("SELECT * FROM ".getDBPrefix()."_warnings WHERE user_id = '".$userid."'");}function getWarningByID($warnid){	$warnid = makeStringSafe($warnid);	return doQuery("SELECT * FROM ".getDBPrefix()."_warnings WHERE warn_id = '".$warnid."'");}function getActiveWarningsForUser($userid){	$userid = makeStringSafe($userid);	return doQuery("SELECT * FROM ".getDBPrefix()."_warnings WHERE user_id = '".$userid."' and type='".RES_WARNING_ACTIVE."'");}?>